select.where:
del_flag=0
           and project_id = #{projectId}
            and project_name = #{projectName}
            and customer_id = #{customerId}
            and status = #{status}
            and amount = #{amount}
            and pm_user_id = #{pmUserId}
            and delivery_date = #{deliveryDate}
            and deal_flag = #{dealFlag}
            and remark = #{remark}

mapper.select:
,c.customer_name,u.user_name
from sys_project p
left join sys_customer c on p.customer_id=c.customer_id
left join sys_user u on u.user_id=p.pm_user_id
where p.del_flag=0
     and p.project_id = #{projectId}
     and p.project_name like concat('%',#{projectName},'%')
     and p.customer_id = #{customerId}
     and p.status = #{status}
     and p.amount = #{amount}
     and p.pm_user_id = #{pmUserId}
     and delivery_date = #{deliveryDate}
     and deal_flag = #{dealFlag}

        AND date_format(p.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        AND date_format(p.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

     and p.remark = #{remark}

mapper.update:
update sys_project
project_name = #{projectName}, customer_id = #{customerId}, status = #{status}, amount = #{amount}, pm_user_id = #{pmUserId}, delivery_date = #{deliveryDate}, deal_flag = #{dealFlag}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where project_id = #{projectId}
